# Products table

DROP TABLE IF EXISTS `products`;
CREATE TABLE `products` (
	`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
	`name` VARCHAR(225) NOT NULL,
	`model` VARCHAR(50) NULL,
	`category_id` INT(11) UNSIGNED NOT NULL,
	`order` INT(3) default '0',
	`image` VARCHAR(50) NULL default NULL,
	`description` TEXT NULL default NULL,
	`price` VARCHAR(20) NOT NULL default '0',
	`created` DATETIME default NULL,
	`modified` DATETIME default NULL,
	`pounds` INT(5) default '0',
	`ounces` INT(5) default '0',
	`status` ENUM('0','1','2') default '1',
	PRIMARY KEY (`id`),
	FOREIGN KEY (`category_id`) REFERENCES categories(`id`)
);

# Products test data

INSERT INTO `products` (`id`,`name`,`model`,`category_id`,`order`,`image`,`description`,`price`,`created`,`modified`,`pounds`,`ounces`,`status`) VALUES ('1','Celestron AstroMaster 114 EQ Reflector Telescope','114 EQ','4','0','cele248.jpg','<!-- Generated by XStandard version 2.0.0.0 on 2008-08-19T16:37:43 --><div class=\"clearfix\" id=\"itemDetails\"><p>If you\'re looking for a dual-purpose telescope appropriate for both terrestrial and celestial viewing, then the AstroMaster Series is for you. Each AstroMaster model is capable of giving correct views of land and sky. The AstroMaster Series produces bright, clear images of the moon and planets.</p><p>You won\'t believe how easy it is to see the moons of Jupiter and the rings of Saturn with every one of these fine instruments. When used for astronomical viewing, AstroMasters yield breathtaking views of the moon, Saturn with its ring structure, Jupiter and its belts and moons, nebulae, and star clusters.</p><p>The Celestron AstroMaster 114 EQ Reflector, in particular, is ideal for the novice skywatcher. It features all-glass optical elements, smooth operating steel tripod mountings with manual motion controls, and coated optics for enhanced image brightness and clarity. Quick-release, no-tool dovetail attachment makes setup a breeze, and a rugged pre-assembled tripod with 1.25-inch steel tube legs provides a rigid and stable platform.</p><p>Equipped with a permanently mounted StarPointer Finderscope lets you keep track of the stars, and a German Equatorial Mount with Setting Circles helps you accurately locate and track sky objects. A deluxe accessory tray provides convenient storage of accessories, and the included &quot;The Sky&quot; Level 1 planetarium software features a 10,000-object database and enhanced images.</p><p>This telescope combines the best in intricate technology and ease of use, and makes a great gift for any budding astronomer, young or old.</p><p><strong>Features:</strong></p><ul><li>Quick and easy no-tool setup</li><li>Permanently mounted StarPointer</li><li>Erect image optics - Ideal for terrestrial and astronomical use</li><li>Quick release doevetail attachment - no tool setup</li><li>German Equatorial Mount with Setting Circles - to accurately locate and track sky objects</li><li>Rugged pre-assembled tripod with 1.25&quot; steel tube legs - Provides a rigid and stable platform</li><li>All coated glass optics for clear, crisp images</li><li>Deluxe accessory tray for convenient storage of accessories</li><li>The Sky Level 1 planetarium software with 10,000 object database and enhanced images</li></ul><p>&#160;</p><div class=\"prodSpecs\"><table border=\"0\" cellpadding=\"4\" width=\"365\"><tbody><tr class=\"rowHighlight\"><td valign=\"top\">Recommended Usage:</td><td>Astrophotography , Viewing Galaxies/Star Clusters , Viewing Nebulae , Viewing the Moon , Viewing the Planets</td></tr><tr><td valign=\"top\">Warranty:</td><td>2-Year Limited</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Brand:</td><td>Celestron</td></tr><tr><td valign=\"top\">Usage:</td><td>For the Beginner</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Weight Complete:</td><td>17 lbs.</td></tr><tr><td valign=\"top\">Finderscope:</td><td>Fixed StarPointer</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Lens Coating:</td><td>Aluminum</td></tr><tr><td valign=\"top\">GPS:</td><td>No</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">GoTo:</td><td>No</td></tr><tr><td valign=\"top\">Good for Astrophotography:</td><td>Yes</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Telescopes Series:</td><td>AstroMaster Series Telescopes</td></tr><tr><td valign=\"top\">Optical Design:</td><td>Reflector</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Aperture:</td><td>114mm</td></tr><tr><td valign=\"top\">Focal Length:</td><td>1000mm</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Focal Ratio:</td><td>f/9</td></tr><tr><td valign=\"top\">Mount Design:</td><td>CG-2 Equatorial</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Eyepiece 1:</td><td>20mm (50x)</td></tr><tr><td valign=\"top\">Eyepiece 2:</td><td>10mm (100x)</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Highest Useful Magnification:</td><td>200x</td></tr><tr><td valign=\"top\">Star Diagonal:</td><td>1.25 Inch</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">User Level:</td><td>For the Beginner</td></tr></tbody></table></div><div class=\"prodSpecs\"><p>&#160;</p></div></div>','149.99','2008-08-06 20:05:49','2008-08-19 16:37:43','2','0','1');
INSERT INTO `products` (`id`,`name`,`model`,`category_id`,`order`,`image`,`description`,`price`,`created`,`modified`,`pounds`,`ounces`,`status`) VALUES ('2','Meade DS-2130LNT Reflector Telescope with LNT AutoAlign Technology','DS-2130LNT','4','0','meade2130.jpg','<!-- Generated by XStandard version 2.0.0.0 on 2008-08-19T16:53:06 --><p><strong>Meade DS-2130ATS-LNT?</strong> 127mm (5.1-inch) Altazimuth Reflector Telescope</p><p>The DS-2130ATS-LNT is a fully equipped telescope. Its on-board computer knows the night sky. After a short alignment procedure, your telescope will be ready to take you on a tour of the universe. You will see more objects in one night than Galileo saw in a lifetime. See planets like Saturn and Jupiter, star clusters, nebulae, galaxies, and more.</p><ul><li><strong>Large 127mm Aperture</strong><br /> <span class=\"text\">Captures more light for brighter images and greater detail (469% more light than a 60mm telescope)</span></li><li><strong>2Premium 1.25-inch super Pl√∂ssl eyepieces</strong><br /> <span class=\"text\">For superior long eye relief and an expansive wide visual field</span></li><li><strong>SmartFinder</strong><br /> <span class=\"text\">Electronic level sensor, electronic magnetic north sensor, high-precision internal clock, and red-dot projection finder all work together to get you aligned with the heavens</span></li><li><strong>AutoStar Suite Astronomer Edition Software</strong> Amazing planetarium software and instructional video will teach you the night sky and how to use your telescope</li><li><strong>Assembles in Minutes</strong> Includes everything you need to explore your universe</li></ul>','399.99','2008-08-06 20:05:49','2008-08-25 15:19:09','0','0','1');
INSERT INTO `products` (`id`,`name`,`model`,`category_id`,`order`,`image`,`description`,`price`,`created`,`modified`,`pounds`,`ounces`,`status`) VALUES ('4','Celestron NexStar 130 SLT Telescope','130 SLT','4','0','cele026.jpg','<!-- Generated by XStandard version 2.0.0.0 on 2008-08-24T12:16:25 --><h1><span class=\"paragraphHeader\"><a class=\"anchorFix\" id=\"details\"></a>Product Details</span></h1><p>The popularity of our NexStar 114 models inspired us to go bigger! We are proud to introduce NexStar 130 SLT. The NexStar 130 SLT has 30% more light-gathering power than our 114mm telescope.</p><p>And the 130 SLT, like the other models in the SLT Series, comes with a fully computerized hand control. The computerized hand control gives you the ability to automatically track to view any of its 4,000+ objects, including over 600 galaxies, 300 clusters, and dozens of beautiful binary stars.</p><p>With its pre-assembled, adjustable steel tripod, the NexStar 130 SLT can be up and ready to use in a matter of minutes. Our new SkyAlign alignment technology and the included StarPointer Finderscope with a red LED makes aligning a breeze.</p><p>View the details of the lunar surface, the rings of Saturn, the polar ice caps on Mars, the cloud belts on Jupiter, or a number of the Messier objects such as the globular cluster in Hercules (M13), or the Great Nebula in Orion (M42). Begin to explore some of the fainter Messier objects using the additional light-gathering capabilities of the 130 SLT&#8217;s 5&quot; primary mirror.</p><p>Because of the Newtonian design, the mirror gives fully color-corrected views that are best suited for astronomical use.</p><p><span>Portable, User-friendly Design</span></p><p>Powered by 8 AA user-supplied batteries or an optional AC adapter, this NexStar telescope is ready to travel. The internal battery compartment provides power to the high precision servo motors for rigid low-vibration performance while eliminating cord wrap issues associated with external battery packs. With the NexStar\'s ergonomically-designed hand control, the user is free to remove the hand control from its holder for remote use, or leave it cradled for hands-free operation. With a touch of a button you can select the object catalog, change the slew speed, view fascinating information about an object, or simply know if a desired object is visible in the sky.<br /><br />Celestron\'s Revolutionary SkyAlign</p><p><br />Using Celestron\'s patented <a href=\"http://www.telescopes.com/telescopes/celestronskyaligntelescopesskyalignarticle.cfm\">SkyAlign</a>, simply input the date, time, and location into the hand control then slew the telescope to any three bright celestial objects in the sky. And that\'s it - alignment so easy it\'s hard to believe! You\'ll spend your evening observing, not aligning your telescope!</p><p><strong>General</strong></p><ul><li>Compatible with 2-inch eyepieces</li><li>High-quality 130mm reflector</li><li>Fully computerized altazimuth mount</li><li>StarPointer finderscope to help with alignment and accurately locating objects</li><li>Quick-release fork arm mount, optical tube, and accessory tray for quick no-tool set up</li><li>Sturdy stainless steel tripod and accessory tray included</li><li>Includes CD-ROM &quot;The Sky&quot; Astronomy Software, which provides education about the sky and printable sky maps</li></ul><p><strong>Computerized</strong></p><ul><li>Proven NexStar computer control technology</li><li>Database allows telescope to locate over 4,000 celestial objects</li><li>SkyAlign allows you to align on any three bright celestial objects, making for a fast and easy alignment process</li><li>Flash-upgradable hand control software and motor control units for downloading product updates over the Internet</li><li>Internal battery compartment to prevent cord wrap during use</li><li>Auxiliary port for additional accessories such as <a href=\"http://www.telescopes.com/telescope-accessories/maps-books-software/celestrongpsaccessorycn16fornexstaritelescopes.cfm\" title=\"Celestron GPS Accessory\">GPS accessory</a></li><li>Includes NSOL telescope control software for basic control of your telescope via computer (with optional <a href=\"http://www.telescopes.com/telescope-accessories/maps-books-software/celestroncablenexstartelescopestors232port.cfm\" title=\"Celestron RS-232 Cable\">RS-232 cable</a>)</li><li>Compatible with optional <a href=\"http://www.telescopes.com/telescope-accessories/maps-books-software/celestronnexremotetelescopecontrolsoftwarepackage.cfm\" title=\"Celestron NexRemote Telescope Control Software\">NexRemote telescope control software</a> for advanced control of your telescope via computer</li></ul><p><a href=\"http://www.jb.man.ac.uk/public/im/cel130SLT.html\" title=\"Celestron NexStar 130 SLT Review\">Read an in-depth review of the Celestron NexStar 130SLT</a> by Ian Morion, Jodrell Bank Observatory<br /><br /></p><p><a href=\"/images/pdf/CELE026.pdf\">Download Celestron NexStar 130 SLT Instruction Manual</a></p><div class=\"prodSpecs\"><table border=\"0\" cellpadding=\"4\" width=\"365\"><tbody><tr class=\"rowHighlight\"><td valign=\"top\">Recommended Usage:</td><td>Astrophotography , Viewing Galaxies/Star Clusters , Viewing Nebulae , Viewing the Moon , Viewing the Planets</td></tr><tr><td valign=\"top\">Warranty:</td><td>2-Year Limited</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Features:</td><td>GoTo</td></tr><tr><td valign=\"top\">Brand:</td><td>Celestron</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Usage:</td><td>For the Enthusiast</td></tr><tr><td valign=\"top\">Weight Complete:</td><td>18 lbs.</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Finderscope:</td><td>StarPointer</td></tr><tr><td valign=\"top\">Lens Coating:</td><td>Standard</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">GoTo:</td><td>Yes</td></tr><tr><td valign=\"top\">Good for Astrophotography:</td><td>Yes</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Telescopes Series:</td><td>NexStar SLT</td></tr><tr><td valign=\"top\">Optical Design:</td><td>Reflector</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Aperture:</td><td>130mm</td></tr><tr><td valign=\"top\">Focal Length:</td><td>650mm</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Focal Ratio:</td><td>f/5</td></tr><tr><td valign=\"top\">Mount Design:</td><td>Computerized GOTO</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Battery Included:</td><td>No</td></tr><tr><td valign=\"top\">Eyepiece 1:</td><td>25mm (26x)</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Eyepiece 2:</td><td>9mm (72x)</td></tr><tr><td valign=\"top\">Highest Useful Magnification:</td><td>300x</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Limiting Magnitude:</td><td>13.1</td></tr><tr><td valign=\"top\">Resolution:</td><td>1.06 Arc Seconds</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">Resolving Power:</td><td>.89 Arc Seconds</td></tr><tr><td valign=\"top\">Star Diagonal:</td><td>1.25 inch</td></tr><tr class=\"rowHighlight\"><td valign=\"top\">User Level:</td><td>For the Enthusiast</td></tr></tbody></table></div>','439.00','2008-08-21 13:58:52','2008-08-25 15:19:14','18','0','1');
INSERT INTO `products` (`id`,`name`,`model`,`category_id`,`order`,`image`,`description`,`price`,`created`,`modified`,`pounds`,`ounces`,`status`) VALUES ('3','Meade DS-2080LNT Refractor Telescope with LNT AutoAlign Technology','DS-2080LNT','4','0','DS-2080LNT.jpg','<!-- Generated by XStandard version 2.0.0.0 on 2008-08-19T16:41:44 --><ul><li>Fully GoTo-enabled refractor telescope</li><li>Automatically locates and tracks thousands of objects</li><li>Includes AutoStar Suite Astronomer Edition Software</li><li>Large, 80mm refractive lens</li><li>Premium Plossl eyepieces for long-wear relief</li><li>New generation II DS-2000 mount</li><li>New advanced field tripod for a solid observing platform</li><li>Oversized declination lock knob with fast locking grip<br /></li></ul>','349.00','2008-08-06 20:05:49','2008-08-19 16:41:44','1','2','1');
INSERT INTO `products` (`id`,`name`,`model`,`category_id`,`order`,`image`,`description`,`price`,`created`,`modified`,`pounds`,`ounces`,`status`) VALUES ('6','Celestron NexStar 60 SLT Telescope','CELE022','7','0','next60.jpg','<p><span class=\"paragraphHeader\"><a class=\"anchorFix\" name=\"details\"></a>Product Details</span></p>\r\n<p><br /><span style=\"font-size: x-small;\"><strong>Completely new! </strong></span><br /><br />Designed to be an affordable entry-level to mid-level, computerized GOTO telescope, the <a title=\"Celestron NexStar 60 SLT Telescope\" href=\"http://www.telescopes.com/telescopes/products/celestron-nexstar-60-slt-32977.html\">Celestron NexStar 60 SLT Refractor</a> is loaded with valuable design features. With pre-assembled, adjustable stainless steel tripods, and quick-release fork arms and tubes, the <a title=\"Celestron NexStar SLT Telescopes\" href=\"http://www.telescopes.com/series/series_4053.html\">NexStar SLT</a> telescope can be set up in a matter of minutes - no tools required! <br /><br />It\'ll also give you stunning views! You\'ll see details of the lunar surface, Venus and its phases, Mars resolved as an orange disc, Jupiter and four moons, Saturn with rings plainly visible and much more! And the <a title=\"Celestron NexStar 60SLT\" href=\"http://www.telescopes.com/telescopes/products/celestron-nexstar-60-slt-32977.html\">NexStar 60 SLT</a> can be used as a land-based spotting scope, as well. <br /><br /><span style=\"font-size: x-small;\"><span style=\"font-weight: bold;\">Intelligent Design</span></span><br /><br />Powered by 8 AA user supplied batteries, an <a title=\"Celestron AC Adapter\" href=\"http://www.telescopes.com/telescopes/products/celestron-ac-adapter-for-computerized-celestron-telescopes-42937.html\">optional AC adapter</a>, <a title=\"Celestron Car Battery Adapter\" href=\"http://www.telescopes.com/telescopes/products/celestron-car-battery-adapter-for-all-nexstars-12047.html\">optional car-battery adapter</a>, or <a title=\"Celestron Power Tank\" href=\"http://www.telescopes.com/telescopes/products/celestron-power-tank-12v-power-supply-7-amp-hour-15740.html\">powertank power source</a>, the NexStar GOTO loves to travel. The internal battery compartment provides power to the high-precision servomotors for smooth, low-vibration performance while eliminating cord-wrap issues associated with external battery packs. <br /><br />With the NexStar\'s ergonomically engineered hand controller, the user is free to remove the hand control from its holder for remote use or leave it cradled for hands-free operation. With a touch of a button you can select the object catalog, change the slew speed, view fascinating information about an object, or simply find out whether a desired object is visible in the sky. <br /><br /><span style=\"font-size: x-small;\"><span style=\"font-weight: bold;\">Celestron\'s Revolutionary <a href=\"http://www.telescopes.com/telescopes/celestronskyaligntelescopesskyalignarticle.cfm\">SkyAlign</a> </span></span><br /><br />Using Celestron\'s patented <a href=\"http://www.telescopes.com/telescopes/celestronskyaligntelescopesskyalignarticle.cfm\">SkyAlign</a>, simply input the date, time and location into the hand control then slew the telescope to any three bright celestial objects in the sky. And that\'s it - alignment so easy it\'s hard to believe! You\'ll spend your evening observing, not aligning your telescope!</p>\r\n<p><span style=\"font-size: x-small;\"><span style=\"font-weight: bold;\">Standard Features and Accessories</span></span></p>\r\n<div id=\"itemDetails\" class=\"clearfix\">\r\n<li>Computerized hand controller with 4000-plus-object database; <br /></li>\r\n<li>Quick-release fork-arm mount, optical tube and accessory tray for quick set up; <br /></li>\r\n<li><a href=\"http://www.telescopes.com/telescopes/celestronskyaligntelescopesskyalignarticle.cfm\">SkyAlign</a> for easy alignment on any three bright celestial objects; <br /></li>\r\n<li>Comes with \"The Sky\" Level 1 planetarium software and NSOL telescope control software for controlling the telescope via computer; requires the <a title=\"Celestron Computer Cable\" href=\"http://www.telescopes.com/telescopes/products/celestron-cable-nexstar-telescopes-to-rs-232-port-20251.html\">optional Celestron Computer Cable</a> <br /></li>\r\n<li>Internal battery compartment to prevent cord wrap during use; <br /></li>\r\n<li>Sturdy stainless steel tripod; <br /></li>\r\n<li>Starpointer finder scope to help with alignment; <br /></li>\r\n<li>Auxiliary port for additional accessories such as the <a title=\"Celestron GPS Accessory\" href=\"http://www.telescopes.com/telescopes/products/celestron-gps-accessory-cn-16-for-nexstar-i-telescopes-12024.html\">optional GPS accessory</a>; <br /></li>\r\n<li>Motorized altazimuth mount and fully computerized hand controller; <br /></li>\r\n<li>US and International city database for easy location settings, <br /></li>\r\n<li>Flash upgradeable hand-controller software and motor-control units.</li>\r\n<p>Ôæ†</p>\r\n<p>Ôæ†</p>\r\n<p><a href=\"http://www.telescopes.com/images/pdf/CELE022.pdf\" target=\"_blank\">Download Celestron NexStar 60 SLT Instruction Manual</a></p>\r\n<div class=\"prodSpecs\">\r\n<table border=\"0\" cellpadding=\"4\" width=\"365\">\r\n<tbody>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Recommended Usage:</td>\r\n<td>Astrophotography , Viewing the Moon , Viewing the Planets</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Warranty:</td>\r\n<td>2-Year Limited</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Features:</td>\r\n<td>GoTo</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Brand:</td>\r\n<td>Celestron</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Usage:</td>\r\n<td>For the Enthusiast</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Weight Complete:</td>\r\n<td>9.5 lbs.</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Finderscope:</td>\r\n<td>StarPointer</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Lens Coating:</td>\r\n<td>Standard</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">GoTo:</td>\r\n<td>Yes</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Good for Astrophotography:</td>\r\n<td>Yes</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Telescopes Series:</td>\r\n<td>NexStar SLT</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Optical Design:</td>\r\n<td>Refractor</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Aperture:</td>\r\n<td>60mm</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Focal Length:</td>\r\n<td>700mm</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Focal Ratio:</td>\r\n<td>f/12</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Mount Design:</td>\r\n<td>Computerized GOTO</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Battery Included:</td>\r\n<td>No</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Eyepiece 1:</td>\r\n<td>25mm (28x)</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Eyepiece 2:</td>\r\n<td>9mm (78x)</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Highest Useful Magnification:</td>\r\n<td>142x</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Limiting Magnitude:</td>\r\n<td>11.4</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Resolution:</td>\r\n<td>2.31 Arc Seconds</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Resolving Power:</td>\r\n<td>1.93 Arc Seconds</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Star Diagonal:</td>\r\n<td>1.25 inch</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">User Level:</td>\r\n<td>For the Enthusiast</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>','439.00','2008-08-28 12:53:26','2008-08-28 12:53:26','9','5','1');
INSERT INTO `products` (`id`,`name`,`model`,`category_id`,`order`,`image`,`description`,`price`,`created`,`modified`,`pounds`,`ounces`,`status`) VALUES ('7','Celestron PowerSeeker 80 EQ Telescope','CELE016 ','7','0','power.jpg','<p><span class=\"paragraphHeader\"><a class=\"anchorFix\" name=\"details\"></a>Product Details</span></p>\r\n<p><strong><em>Premium performer!</em></strong><br /><br />PowerSeeker telescopes are a great way to open up the wonders of the universe to the aspiring astronomer!<br /><br />The <strong>Celestron PowerSeeker 80 EQ telescope</strong> is designed to give the first-time buyer a fine combination of quality, value, features, and power. Offering exceptional value, the PowerSeeker 80 EQ is portable yet powerful, offering a full 80mm of optical performance to turn on any newcomer to the world of amateur astronomy. <br /><br />The PowerSeeker 80 EQ comes with high-quality, all-glass optical components and high-transmission coatings for enhanced image brightness and clarity. It offers the light-gathering power needed to resolve hundreds of celestial objects, including some deep-sky star clusters and galaxies. <br /><br /><span style=\"padding: 5px; background-color: #cccccc;\"><strong>High-performance features<br /></strong></span></p>\r\n<div id=\"itemDetails\" class=\"clearfix\">\r\n<li>Sturdy equatorial mount for easy tracking</li>\r\n<li>5x24 finderscope</li>\r\n<li>20mm (45X), and 4mm (225X) eyepieces.<br /><br />The Celestron PowerSeeker 80 EQ: it\'s a stellar choice for star-minded children and families!<br /><br />Navigate the night sky with <a href=\"http://www.telescopes.com/products/Celestron_Sky_Maps__12099.html\">Celestron Sky Maps</a>.</li>\r\n<div class=\"prodSpecs\">\r\n<table border=\"0\" cellpadding=\"4\" width=\"365\">\r\n<tbody>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Recommended Usage:</td>\r\n<td>Astrophotography , Viewing the Moon , Viewing the Planets</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Brand:</td>\r\n<td>Celestron</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Usage:</td>\r\n<td>For the Beginner</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Weight Complete:</td>\r\n<td>19 lbs.</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Finderscope:</td>\r\n<td>5x24</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Lens Coating:</td>\r\n<td>Standard</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">GPS:</td>\r\n<td>No</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">GoTo:</td>\r\n<td>No</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Good for Astrophotography:</td>\r\n<td>Yes</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Telescopes Series:</td>\r\n<td>PowerSeeker Telescopes</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Optical Design:</td>\r\n<td>Refractor</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Aperture:</td>\r\n<td>80mm</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Focal Length:</td>\r\n<td>900mm</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Focal Ratio:</td>\r\n<td>f/11</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Mount Design:</td>\r\n<td>German Equatorial</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Eyepiece 1:</td>\r\n<td>20mm (45x)</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Eyepiece 2:</td>\r\n<td>4mm (225x)</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Highest Useful Magnification:</td>\r\n<td>225x</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Limiting Magnitude:</td>\r\n<td>12</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Resolution:</td>\r\n<td>1.73 Arc Seconds</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">Resolving Power:</td>\r\n<td>1.45 Arc Seconds</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">Star Diagonal:</td>\r\n<td>1.25 inch</td>\r\n</tr>\r\n<tr class=\"rowHighlight\">\r\n<td valign=\"top\">User Level:</td>\r\n<td>For the Beginner</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>','208.95','2008-08-28 13:00:03','2008-08-28 13:11:32','19','0','1');

# Cart Table

DROP TABLE IF EXISTS `carts`;
CREATE TABLE `carts` (
	`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
	`user_id` INT(11) UNSIGNED NOT NULL,
	`session` VARCHAR(225) NOT NULL,
	`created` DATETIME default null,
	PRIMARY KEY (`id`),
	FOREIGN KEY (`user_id`) REFERENCES users(`id`)
);

# Cart Items Table

DROP TABLE IF EXISTS `cart_items`;
CREATE TABLE `cart_items` (
	`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
	`cart_id` INT(11) UNSIGNED NOT NULL,
	`product_id` INT(11) UNSIGNED NOT NULL,
	`price` VARCHAR(50) NOT NULL,
	`quantity` INT(11) NOT NULL,
	`created` DATETIME default NULL,
	`modified` DATETIME default NULL,
	PRIMARY KEY (`id`),
	FOREIGN KEY (`cart_id`) REFERENCES carts(`id`),
	FOREIGN KEY (`product_id`) REFERENCES products(`id`)
);

# Users Table
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
	`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
	`password` VARCHAR(50) NOT NULL,
	`first` VARCHAR(50) NOT NULL,
	`last` VARCHAR(50) NOT NULL,
	`email` VARCHAR(50) NOT NULL,
	`phone` VARCHAR(50) NULL,
	`created` DATETIME default null,
	`admin` ENUM('0', '1') default 0,
	`company` VARCHAR(50) NULL,
	 PRIMARY KEY (`id`)
);

