# Coupons table
DROP TABLE IF EXISTS `coupons`;
CREATE TABLE `coupons` (
	`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
	`code` VARCHAR(50) NOT NULL,
	`percent` VARCHAR(8) NULL,
	`start` DATE default null,
